"use strict";(self.webpackChunkdocumentation=self.webpackChunkdocumentation||[]).push([[5588],{9082:function(n,e,t){t.r(e),t.d(e,{contentTitle:function(){return c},default:function(){return b},exampleString:function(){return d},frontMatter:function(){return l},metadata:function(){return u},toc:function(){return m}});var o=t(7462),r=t(3366),a=(t(7294),t(3905)),s=(t(9055),t(8909)),i=["components"],l={},c=void 0,u={unversionedId:"Engineering/FrontEnd/ComponentsSection/CarouselComponents/CarouselTabBar",id:"Engineering/FrontEnd/ComponentsSection/CarouselComponents/CarouselTabBar",title:"CarouselTabBar",description:"export const exampleString = `<CarouselTabNav.Navigator",source:"@site/docs/Engineering/FrontEnd/ComponentsSection/CarouselComponents/CarouselTabBar.mdx",sourceDirName:"Engineering/FrontEnd/ComponentsSection/CarouselComponents",slug:"/Engineering/FrontEnd/ComponentsSection/CarouselComponents/CarouselTabBar",permalink:"/va-mobile-app/docs/Engineering/FrontEnd/ComponentsSection/CarouselComponents/CarouselTabBar",tags:[],version:"current",frontMatter:{},sidebar:"tutorialSidebar",previous:{title:"Carousel",permalink:"/va-mobile-app/docs/Engineering/FrontEnd/ComponentsSection/CarouselComponents/Carousel"},next:{title:"BasicError",permalink:"/va-mobile-app/docs/Engineering/FrontEnd/ComponentsSection/ErrorComponents/BasicError"}},m=[],d='<CarouselTabNav.Navigator \ntabBar={(props): \nReact.ReactNode => <CarouselTabBar {...props} \nonCarouselEnd={onCarouselEnd} \ntranslation={translation} \nscreenList={screenList} />}>\n    <CarouselTabNav.Screen \n    name="Main" \n    children={(): ReactElement => <CarouselStackComponent screenList={screenList} />} \n    options={{ headerShown: false }} />\n</CarouselTabNav.Navigator>',p={toc:m,exampleString:d};function b(n){var e=n.components,t=(0,r.Z)(n,i);return(0,a.kt)("wrapper",(0,o.Z)({},p,t,{components:e,mdxType:"MDXLayout"}),(0,a.kt)(s.Z,{componentName:"CarouselTabBar",example:d,codeString:"import { Pressable } from 'react-native'\nimport React, { FC, ReactElement, useState } from 'react'\n\nimport { BottomTabNavigationEventMap } from '@react-navigation/bottom-tabs/lib/typescript/src/types'\nimport { NavigationHelpers, ParamListBase } from '@react-navigation/native'\nimport { SafeAreaView } from 'react-native-safe-area-context'\nimport { TFunction } from 'i18next'\nimport _ from 'underscore'\nimport styled from 'styled-components'\n\nimport { Box, BoxProps, TextView } from '../index'\nimport { CarouselScreen } from './Carousel'\nimport { a11yHintProp, testIdProps } from 'utils/accessibility'\nimport { themeFn } from 'utils/theme'\nimport { useTheme } from 'utils/hooks'\n\nconst StyledSafeAreaView = styled(SafeAreaView)`\n  background-color: ${themeFn((theme) => theme.colors.background.carousel)};\n`\n\nconst StyledPressable = styled(Pressable)`\n  min-height: ${themeFn((theme) => theme.dimensions.touchableMinHeight)}px;\n  justify-content: center;\n`\n\ntype CarouselTabBarProps = {\n  /** the tab navigators navigation helpers */\n  navigation: NavigationHelpers<ParamListBase, BottomTabNavigationEventMap>\n\n  /** called when the skip button is clicked or the user has gone through all the carousel components */\n  onCarouselEnd: () => void\n\n  /** useTranslations t function to translate the labels */\n  translation: TFunction\n\n  /** list of screens with the screen name and the component in each item */\n  screenList: Array<CarouselScreen>\n}\n\n/**A common component with the carousel tab bar content. Displays skip button, continue button, and a progress bar*/\nconst CarouselTabBar: FC<CarouselTabBarProps> = ({ navigation, onCarouselEnd, screenList, translation }) => {\n  const theme = useTheme()\n  const [currentScreenIndex, setCurrentScreenIndex] = useState(0)\n  const a11yHints = screenList[currentScreenIndex].a11yHints\n\n  const onContinue = (): void => {\n    const updatedIndex = currentScreenIndex + 1\n\n    if (updatedIndex === screenList.length) {\n      onCarouselEnd()\n      return\n    }\n\n    setCurrentScreenIndex(updatedIndex)\n    navigation.navigate(screenList[updatedIndex].name)\n  }\n\n  const goBack = (): void => {\n    const updatedIndex = currentScreenIndex - 1\n    setCurrentScreenIndex(updatedIndex)\n    navigation.navigate(screenList[updatedIndex].name)\n  }\n\n  const getProgressBar = (): ReactElement[] => {\n    return _.map(screenList, (screen, index) => {\n      const boxProps: BoxProps = {\n        width: 12,\n        height: 12,\n        borderRadius: 6,\n        opacity: index === currentScreenIndex ? 1 : 0.5,\n        m: theme.dimensions.carouselProgressDotsMargin,\n        backgroundColor: 'textBox',\n      }\n\n      return <Box {...boxProps} key={index} />\n    })\n  }\n\n  const goBackOrSkipBtn = () => {\n    let onPressCallback: TFunction\n    let buttonText: string\n    let allyHint: string | undefined\n\n    if (currentScreenIndex === 0) {\n      onPressCallback = onCarouselEnd\n      buttonText = 'common:skip'\n      allyHint = a11yHints?.skipHint\n    } else {\n      onPressCallback = goBack\n      buttonText = 'common:back'\n      allyHint = a11yHints?.backHint\n    }\n\n    return (\n      <StyledPressable onPress={onPressCallback} accessibilityRole=\"button\" {...testIdProps(translation(buttonText))} {...a11yHintProp(allyHint || '')}>\n        <TextView variant=\"MobileBody\" color=\"primaryContrast\" allowFontScaling={false} mr=\"auto\" selectable={false}>\n          {translation(buttonText)}\n        </TextView>\n      </StyledPressable>\n    )\n  }\n\n  const nextOrDoneBtn = () => {\n    let buttonText: string\n    let allyHint: string | undefined\n\n    if (currentScreenIndex === screenList.length - 1) {\n      buttonText = 'common:done'\n      allyHint = a11yHints?.doneHint\n    } else {\n      buttonText = 'common:next'\n      allyHint = a11yHints?.continueHint\n    }\n\n    return (\n      <StyledPressable onPress={onContinue} accessibilityRole=\"button\" {...testIdProps(translation(buttonText))} {...a11yHintProp(allyHint || '')}>\n        <TextView variant=\"MobileBodyBold\" color=\"primaryContrast\" allowFontScaling={false} ml=\"auto\" selectable={false}>\n          {translation(buttonText)}\n        </TextView>\n      </StyledPressable>\n    )\n  }\n\n  const progressBarContainerProps: BoxProps = {\n    flex: 1,\n    display: 'flex',\n    alignItems: 'center',\n    justifyContent: 'center',\n    flexDirection: 'row',\n    accessibilityRole: 'progressbar',\n    accessible: true,\n    minHeight: theme.dimensions.touchableMinHeight,\n  }\n\n  return (\n    <StyledSafeAreaView edges={['bottom']}>\n      <Box display=\"flex\" flexDirection=\"row\" height={70} backgroundColor=\"carousel\" alignItems=\"center\" mx={theme.dimensions.gutter}>\n        <Box flex={1} display=\"flex\" justifyContent=\"center\">\n          {goBackOrSkipBtn()}\n        </Box>\n        <Box {...testIdProps(translation('common:carouselIndicators'))} {...a11yHintProp(a11yHints?.carouselIndicatorsHint || '')} {...progressBarContainerProps}>\n          {getProgressBar()}\n        </Box>\n        <Box flex={1} display=\"flex\" justifyContent=\"center\">\n          {nextOrDoneBtn()}\n        </Box>\n      </Box>\n    </StyledSafeAreaView>\n  )\n}\n\nexport default CarouselTabBar\n",mdxType:"ComponentTopInfo"}))}b.isMDXComponent=!0}}]);